/*FUNÇÃO AJAX PARA O BOTÃO DELETAR*/
/*O botão deletar ao receber o evento onclick 
faça o que está a baixo*/

    $('.deletar').on('click', function (event) {
        event.preventDefault();
        var link=$(this).attr('href');
        alert(link);
        /*Link recebe o $(this).attr('href');
        que é o link com ID passado anteriormente*/
        
        if(confirm("Deseja mesmo excluir esse usuário?")){
            $.ajax({
                url: link, 
                success: function(){
                    $('.resultadoExclusao').show().html();
                }
            });
        }else{
            return false;
        }
    });

